import React,{ useState } from "react";
import {
    Box,
    Button,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  SliderTrack,
  Flex,
  Slider,
  SliderFilledTrack,
  SliderThumb
} from "@chakra-ui/react";
function Epoch() {
   const [Quorum, setQuorum] = useState({
        write:0,
        read:0
   });
 const handleChange = (event) => {
   const { name, value } = event.target;
   setQuorum((prev) => {
     return {
       ...prev,
       [name]: value,
     };
   });
 };
  return (
    <Box ml={100} mt={50} maxW={300}>
      <form>
        <Flex>
          <NumberInput
            name="write"
            maxW="100px"
            mr="2rem"
            value={Quorum.write}
            onChange={handleChange}
          >
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
          <Slider
            name="write"
            flex="1"
            focusThumbOnChange={false}
            value={Quorum.write}
            onChange={handleChange}
          >
            <SliderTrack>
              <SliderFilledTrack />
            </SliderTrack>
            <SliderThumb fontSize="sm" boxSize="32px" children={value} />
          </Slider>
        </Flex>
        <Flex mt={50}>
          <NumberInput
            name="read"
            maxW="100px"
            mr="2rem"
            value={Quorum.read}
            onChange={handleChange}
          >
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
          <Slider
            name="read"
            flex="1"
            focusThumbOnChange={false}
            value={Quorum.read}
            onChange={handleChange}
          >
            <SliderTrack>
              <SliderFilledTrack />
            </SliderTrack>
            <SliderThumb fontSize="sm" boxSize="32px" children={Quorum.read} />
          </Slider>
        </Flex>
        <Button type="submit" colorScheme="teal" variant="solid" mt={10}>
          Submit
        </Button>
      </form>
    </Box>
  );
}

export default Epoch;
