import { AnimatePresence, Box, Heading } from "@chakra-ui/react";
import { useEffect, useState } from "react";
import { motion } from "framer-motion";

const variants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0 },
};

const Slide = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          initial="hidden"
          animate="visible"
          exit="hidden"
          variants={variants}
        >
          <Box>
            <Heading size='lg'>Solution benefits</Heading>
            <Heading size='md'>
              Improved Performance, Scalability, Optimized Network Bandwidth
              Usage in Cloud, Better resource utilization
            </Heading>
          </Box>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default Slide;
